// src/api.js
import axios from "axios";

const api = axios.create({
    baseURL: "http://127.0.0.1:8000/",
    withCredentials: true, //
    headers: {
        Accept: "application/json", // ✅ On garde seulement ça
    },
});


export const getCommandes = () => {
    return api.get("/api/commandes");
};


export default api;
// src/api.js
