import React, { useEffect, useState, useContext } from "react";
import Modal from "react-modal";
import api from "../../services/api";
import { AuthContext } from "../../context/AuthContext";
import "./Utilisateurs.css";

// Obligatoire pour l'accessibilité
Modal.setAppElement('#root');

const Utilisateurs = () => {
    const { user } = useContext(AuthContext);
    const [utilisateurs, setUtilisateurs] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const [formData, setFormData] = useState({
        nom: "",
        prenom: "",
        email: "",
        password: "",
        role: "client",
    });

    const [formError, setFormError] = useState(null);
    const [formSuccess, setFormSuccess] = useState(null);
    const [modalIsOpen, setModalIsOpen] = useState(false);

    useEffect(() => {
        fetchUtilisateurs();
    }, []);

    const fetchUtilisateurs = async () => {
        setLoading(true);
        setError(null);
        try {
            const response = await api.get("/utilisateurs");
            setUtilisateurs(response.data);
        } catch (err) {
            setError("Erreur lors du chargement des utilisateurs.");
        } finally {
            setLoading(false);
        }
    };

    const handleDelete = async (id) => {
        if (!window.confirm("Voulez-vous vraiment supprimer cet utilisateur ?")) return;

        try {
            await api.delete(`/utilisateurs/${id}`);
            setUtilisateurs(utilisateurs.filter((u) => u.id !== id));
        } catch {
            alert("Erreur lors de la suppression.");
        }
    };

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setFormError(null);
        setFormSuccess(null);

        try {
            await api.post('/utilisateurs', formData);
            fetchUtilisateurs();
            setFormSuccess("Utilisateur ajouté avec succès !");
            setFormData({
                nom: "",
                prenom: "",
                email: "",
                password: "",
                role: "client",
            });
            setModalIsOpen(false); // Fermer la modal
        } catch (err) {
            if (err.response) {
                setFormError("Erreur : " + (err.response.data.message || "Erreur serveur"));
            } else {
                setFormError("Erreur de connexion au serveur");
            }
        }
    };

    if (!user) {
        return <p>Chargement...</p>;
    }

    return (
        <div>
            <div className="utilisateurs-header">
                <h2>Gestion des utilisateurs</h2>
                <button onClick={() => setModalIsOpen(true)}>+ Ajouter</button>
            </div>

            {error && <p style={{ color: "red" }}>{error}</p>}
            {loading ? (
                <p>Chargement des utilisateurs...</p>
            ) : (
                <table className="table-utilisateurs">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Nom</th>
                        <th>Prénom</th>
                        <th>Email</th>
                        <th>Rôle</th>
                        <th>Actions</th>
                    </tr>
                    </thead>
                    <tbody>
                    {utilisateurs.map((u) => (
                        <tr key={u.id}>
                            <td>{u.id}</td>
                            <td>{u.nom}</td>
                            <td>{u.prenom}</td>
                            <td>{u.email}</td>
                            <td>{u.role}</td>
                            <td>
                                <button onClick={() => handleDelete(u.id)}>Supprimer</button>
                            </td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            )}

            {/* MODAL */}
            <Modal
                isOpen={modalIsOpen}
                onRequestClose={() => setModalIsOpen(false)}
                contentLabel="Ajouter un utilisateur"
                style={{
                    content: {
                        maxWidth: '500px',
                        margin: 'auto',
                        padding: '2rem',
                        height : '50%'
                    }
                }}
            >
                <h3 className="modal-titre">Ajouter un utilisateur</h3>
                {formError && <p style={{ color: "red" }}>{formError}</p>}
                <form onSubmit={handleSubmit} className="modal-content">
                    <div>
                        <input
                            type="text"
                            name="nom"
                            placeholder="Nom"
                            value={formData.nom}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    <div>
                        <input
                            type="text"
                            name="prenom"
                            placeholder="Prénom"
                            value={formData.prenom}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    <div>
                        <input
                            type="email"
                            name="email"
                            placeholder="Email"
                            value={formData.email}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    <div>
                        <input
                            type="password"
                            name="password"
                            placeholder="Mot de passe"
                            value={formData.password}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    <div>
                        <select name="role" value={formData.role} onChange={handleChange}>
                            <option value="admin">Admin</option>
                            <option value="receptionniste">Réceptionniste</option>
                            <option value="client">Client</option>
                        </select>
                    </div>
                    <div className="modal-buttons">
                        <button type="submit" className="btn-submit">Ajouter</button>
                        <button type="button" className="btn-cancel" onClick={() => setModalIsOpen(false)} style={{ marginLeft: "10px" }}>
                            Annuler
                        </button>
                    </div>
                </form>
            </Modal>
        </div>
    );
};

export default Utilisateurs;
