import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import api from '../../services/api';

const FicheProduitAdmin = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [produit, setProduit] = useState(null);
    const [successMessage, setSuccessMessage] = useState('');
    const [errorMessage, setErrorMessage] = useState('');

    useEffect(() => {
        const fetchProduit = async () => {
            try {
                const response = await api.get(`/produits/${id}`);
                setProduit(response.data);
            } catch (err) {
                setErrorMessage("Erreur lors du chargement du produit.");
                console.error(err);
            }
        };

        fetchProduit();
    }, [id]);

    const handleDelete = async () => {
        const confirm = window.confirm("Voulez-vous vraiment supprimer ce produit ?");
        if (!confirm) return;

        try {
            await api.delete(`/produits/${id}`);
            setSuccessMessage("Produit supprimé avec succès !");
            // Redirection vers liste après 2 secondes
            setTimeout(() => {
                navigate("/dashboard/produits");
            }, 2000);
        } catch (err) {
            setErrorMessage("Erreur lors de la suppression du produit.");
        }
    };

    if (!produit) return <p>Chargement...</p>;

    return (
        <div className="container py-5">
            <h2>Détails du Produit</h2>

            {errorMessage && <div className="alert alert-danger">{errorMessage}</div>}
            {successMessage && <div className="alert alert-success">{successMessage}</div>}

            <div className="card mb-3">
                <div className="row g-0">
                    <div className="col-md-4">
                        <img
                            src={`http://127.0.0.1:8000/storage/${produit.image}`}
                            className="img-fluid rounded-start"
                            alt={produit.designation}
                        />
                    </div>
                    <div className="col-md-8">
                        <div className="card-body">
                            <h5 className="card-title">{produit.designation}</h5>
                            <p className="card-text">Référence : {produit.reference}</p>
                            <p className="card-text">Quantité : {produit.quantite}</p>
                            <p className="card-text">Prix : {produit.prix} €</p>

                            <button
                                className="btn btn-warning me-2"
                                onClick={() => navigate(`/dashboard/produits/${produit.id}/edit`)}
                            >
                                Modifier
                            </button>
                            <button className="btn btn-danger" onClick={handleDelete}>
                                Supprimer
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default FicheProduitAdmin;
