// src/components/Sidebar.js
import React from 'react';
import { Link } from 'react-router-dom';
import { AuthContext } from '../../context/AuthContext';
import './Sidebar.css'; // tu peux styliser plus tard

const Sidebar = () => {
    const { user } = useAuth();

    return (
        <div className="sidebar">
            <h3>Tableau de bord</h3>
            <ul>
                {user?.role === 'admin' && (
                    <>
                        <li><Link to="/dashboard/utilisateurs">Gérer les utilisateurs</Link></li>
                        <li><Link to="/dashboard/produits">Gérer les produits</Link></li>
                        <li><Link to="/dashboard/commandes">Gérer les commandes</Link></li>
                        <li><Link to="/dashboard/devis">Gérer les devis</Link></li>
                        <li><Link to="/dashboard/factures">Gérer les factures</Link></li>
                        <li><Link to="/dashboard/stats">Statistiques</Link></li>
                    </>
                )}
                {user?.role === 'receptionniste' && (
                    <>
                        <li><Link to="/dashboard/ajouter-commande">Ajouter une commande</Link></li>
                        <li><Link to="/dashboard/produits">Gérer les produits</Link></li>
                        <li><Link to="/dashboard/clients">Voir les clients</Link></li>
                    </>
                )}
                {user?.role === 'client' && (
                    <>
                        <li><Link to="/dashboard/mes-produits">Mes produits</Link></li>
                        <li><Link to="/dashboard/mes-commandes">Mes commandes</Link></li>
                        <li><Link to="/dashboard/mes-devis">Mes devis</Link></li>
                        <li><Link to="/dashboard/mes-factures">Mes factures</Link></li>
                        <li><Link to="/dashboard/profil">Mon profil</Link></li>
                        <li><Link to="/dashboard/support">Assistance</Link></li>
                    </>
                )}
            </ul>
        </div>
    );
};

export default Sidebar;
