import React, { useEffect, useState } from "react";
import axios from "axios";

const Utilisateurs = () => {
    const [utilisateurs, setUtilisateurs] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchUtilisateurs = async () => {
            try {
                setError(null);

                // 1. Récupérer le cookie CSRF
                await axios.get("http://127.0.0.1:8000/sanctum/csrf-cookie", {
                    withCredentials: true,
                });

                // 2. Récupérer le token depuis localStorage
                const token = localStorage.getItem("token"); // adapter la clé si besoin
                if (!token) {
                    setError("Token non trouvé, veuillez vous connecter");
                    return;
                }

                // 3. Faire la requête avec token dans Authorization
                const response = await axios.get("http://127.0.0.1:8000/api/utilisateurs", {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                    withCredentials: true,
                });

                setUtilisateurs(response.data);
            } catch (err) {
                console.error("Erreur API utilisateurs:", err);
                if (err.response) {
                    setError(
                        `Erreur API: ${err.response.status} - ${err.response.data.message || err.response.statusText}`
                    );
                } else if (err.request) {
                    setError("Erreur: pas de réponse du serveur");
                } else {
                    setError("Erreur: " + err.message);
                }
            }
        };

        fetchUtilisateurs();
    }, []);

    return (
        <div>
            <h2>Gestion des utilisateurs</h2>
            {error && <p style={{ color: "red" }}>{error}</p>}

            <ul>
                {utilisateurs.length === 0 && !error && <li>Aucun utilisateur trouvé.</li>}
                {utilisateurs.map((user) => (
                    <li key={user.id}>
                        {user.nom} {user.prenom} - {user.email} ({user.role})
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Utilisateurs;
