import React, { useEffect, useState } from 'react';
import axios from '../../services/api'; // Vérifie que ce fichier contient bien l'URL de base et les headers

import './Commande.css'; // Tu as déjà ce fichier CSS prêt

const Commande = () => {
    const [commandes, setCommandes] = useState([]);

    useEffect(() => {
        axios.get('/commandes')
            .then(res => {
                setCommandes(res.data);
            })
            .catch(err => {
                console.error("Erreur lors du chargement des commandes :", err);
            });
    }, []);

    return (
        <div className="commandes-container">
            <h2>Liste des Commandes</h2>

            {commandes.map(commande => (
                <div className="commande-card" key={commande.id}>
                    <h3>Commande #{commande.id}</h3>
                    <p><strong>Client :</strong> {commande.client.nom} {commande.client.prenom}</p>
                    <p><strong>Période :</strong> {commande.debut_location} → {commande.fin_location}</p>
                    <p><strong>État :</strong> {commande.etat}</p>
                    <p><strong>Prix total :</strong> {commande.prix_total} €</p>

                    <div className="produits-liste">
                        <strong>Produits :</strong>
                        <ul>
                            {commande.produits.map(prod => (
                                <li key={prod.id}>
                                    {prod.nom} - {prod.pivot.quantite} x {prod.pivot.prix_unitaire} €
                                </li>
                            ))}
                        </ul>
                    </div>
                </div>
            ))}
        </div>
    );
};

export default Commande;
