import React, { useState, useEffect } from 'react';
import axios from 'axios';

// Axios instances
const api = axios.create({
    baseURL: 'http://127.0.0.1:8000/api',
    withCredentials: true,
});

const getCsrfCookie = () => {
    return axios.get('http://127.0.0.1:8000/sanctum/csrf-cookie', { withCredentials: true });
};

export default function Register() {
    const [formData, setFormData] = useState({
        nom: '',
        prenom: '',
        email: '',
        password: '',
        password_confirmation: '',
        adresse: '',
        id_type_client: '',
        role: 'client',  // par défaut
    });
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');

    // Exemple: tu peux charger types_clients ici via api.get('/types-clients')

    const handleChange = (e) => {
        setFormData({...formData, [e.target.name]: e.target.value});
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setSuccess('');

        try {
            await getCsrfCookie(); // Important : appeler cette route d'abord
            await api.post('/register', formData);
            setSuccess('Inscription réussie !');
            setFormData({
                nom: '',
                prenom: '',
                email: '',
                password: '',
                password_confirmation: '',
                adresse: '',
                id_type_client: '',
                role: 'client',
            });
        } catch (err) {
            if (err.response?.data?.errors) {
                setError(JSON.stringify(err.response.data.errors));
            } else if (err.response?.data?.message) {
                setError(err.response.data.message);
            } else {
                setError('Erreur inconnue');
            }
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <input type="text" name="nom" placeholder="Nom" value={formData.nom} onChange={handleChange} required />
            <input type="text" name="prenom" placeholder="Prénom" value={formData.prenom} onChange={handleChange} required />
            <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} required />
            <input type="password" name="password" placeholder="Mot de passe" value={formData.password} onChange={handleChange} required />
            <input type="password" name="password_confirmation" placeholder="Confirmer le mot de passe" value={formData.password_confirmation} onChange={handleChange} required />
            <input type="text" name="adresse" placeholder="Adresse" value={formData.adresse} onChange={handleChange} required />
            <select name="id_type_client" value={formData.id_type_client} onChange={handleChange} required>
                <option value="">Sélectionnez le type client</option>
                {/* Remplace par un vrai fetch depuis API */}
                <option value="1">événementiel</option>
                <option value="2">entreprise</option>
                <option value="3">particulier</option>
            </select>
            <button type="submit">S'inscrire</button>
            {error && <p style={{color: 'red'}}>{error}</p>}
            {success && <p style={{color: 'green'}}>{success}</p>}
        </form>
    );
}



































