import React, { useState } from "react";
import api from "../../services/api";
import axios from "axios";

const Login = () => {
    const [formData, setFormData] = useState({
        email: "",
        password: "",
    });
    const [error, setError] = useState(null);
    const [success, setSuccess] = useState(null);

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(null); // reset l'erreur

        try {
            // 1. Appel CSRF cookie
            await axios.get('http://127.0.0.1:8000/sanctum/csrf-cookie', {
                withCredentials: true,
            });

            // 2. Login
            const response = await api.post('/login', {
                email: formData.email,
                password: formData.password,
            });

            // Stocker token et user dans localStorage
            localStorage.setItem('token', response.data.token);
            localStorage.setItem('user', JSON.stringify(response.data.user));

            setSuccess("Connexion réussie !");

            // À venir : redirection selon rôle (on le fera à l'étape 3)
            const role = response.data.user.role;
            if (role === 'admin') navigate('/admin');
            else if (role === 'client') navigate('/client');
            else if (role === 'receptionniste') navigate('/receptionniste');
            else navigate('/login'); // par défaut



        } catch (err) {
            if (err.response) {
                setError(err.response.data.message || "Erreur serveur");
            } else {
                setError("Erreur de connexion au serveur");
            }
        }
    };

    return (
        <div className="register-content">
            <h2>Connexion</h2>
            {error && <p style={{ color: "red" }}>{error}</p>}
            {success && <p style={{ color: "green" }}>{success}</p>}
            <form onSubmit={handleSubmit}>
                <div className="field">
                <input
                    type="email"
                    name="email"
                    placeholder="Email"
                    value={formData.email}
                    onChange={handleChange}
                    required
                />
                </div>
                <div className="field">
                <input
                    type="password"
                    name="password"
                    placeholder="Mot de passe"
                    value={formData.password}
                    onChange={handleChange}
                    required
                />
                </div>
                <button type="submit">Se connecter</button>
            </form>
        </div>
    );
};

export default Login;
