import React, { useContext } from 'react';
import { AuthContext } from '../../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import './Dashboard.css';

const Dashboard = () => {
    const { user } = useContext(AuthContext);
    const navigate = useNavigate();

    if (!user) {
        return <p>Chargement...</p>;
    }

    const role = user.role;

    return (
        <div className="dashboard-container ">
            <h1>Bienvenue, {user.nom} 👋</h1>
            <p>Rôle : {role}</p>

            {role === 'admin' && (
                <div className="admin-section">
                    <h2>🛠️ Espace Administrateur</h2>
                    <ul>
                        <li><button onClick={() => navigate('/admin/users')}>Gérer les utilisateurs</button></li>
                        <li><button onClick={() => navigate('/produits')}>Gérer les produits</button></li>
                        <li><button onClick={() => navigate('/commandes')}>Gérer les commandes</button></li>
                        <li><button onClick={() => navigate('/devis')}>Gérer les devis</button></li>
                        <li><button onClick={() => navigate('/factures')}>Gérer les factures</button></li>
                        <li><button onClick={() => navigate('/admin/statistiques')}>Voir les statistiques</button></li>
                    </ul>
                </div>
            )}

            {role === 'receptionniste' && (
                <div className="receptionniste-section">
                    <h2>📋 Espace Réceptionniste</h2>
                    <ul>
                        <li><button onClick={() => navigate('/commandes')}>Ajouter une commande</button></li>
                        <li><button onClick={() => navigate('/produits')}>Gérer les produits</button></li>
                        <li><button onClick={() => navigate('/clients')}>Voir les clients</button></li>
                    </ul>
                </div>
            )}
            <div className="container vh-3000" style={{backgroundColor: "#fff"}}>
            {role === 'client' && (


                <div className="dashboard-section">
                    <h2>👤 Espace Client</h2>
                    <ul>
                        <li>📦 Voir mes produits</li>
                        <li>🛒 Voir mes commandes</li>
                        <li>📃 Voir mes devis & factures</li>
                        <li>✏️ Modifier mon profil</li>
                        <li>📞 Contacter l’assistance</li>
                    </ul>
                </div>


            )}
            </div>
        </div>
    );
};

export default Dashboard;
