// src/components/Sidebar.js
import React from 'react';
import { Link } from 'react-router-dom';
import { useAuth  } from '../../context/AuthContext';
import './Sidebar.css'; // tu peux styliser plus tard
import FicheProduitAdmin from "./pages/Produits/FicheProduitAdmin";

// ...
<Route path="/produits/:id" element={<FicheProduitAdmin />} />


const Sidebar = () => {
    const { user } = useAuth();

    return (
        <div className="sidebar">
            <h3>Tableau de bord</h3>
            <nav>
                {user?.role === 'admin' && (
                    <>
                        <Link to="/dashboard/utilisateurs">üë• Utilisateurs</Link>
                        <Link to="/dashboard/produits">üì¶ Produits</Link>
                        <Link to="/dashboard/commandes">üìã Commandes</Link>
                        <Link to="/dashboard/devis">üìù Devis</Link>
                        <Link to="/dashboard/factures">üí∞ Factures</Link>
                        <Link to="/dashboard/statistiques">üìä Statistiques</Link>
                    </>
                )}
                {user?.role === 'receptionniste' && (
                    <>
                        <Link to="/dashboard/ajouter-commande">‚ûï Ajouter commande</Link>
                        <Link to="/dashboard/produits">üì¶ Produits</Link>
                        <Link to="/dashboard/clients">üë§ Clients</Link>
                    </>
                )}
                {user?.role === 'client' && (
                    <>
                        <Link to="/dashboard/mes-produits">üì¶ Mes produits</Link>
                        <Link to="/dashboard/mes-commandes">üìã Mes commandes</Link>
                        <Link to="/dashboard/mes-devis">üìù Mes devis</Link>
                        <Link to="/dashboard/mes-factures">üí∞ Mes factures</Link>
                        <Link to="/dashboard/profil">üë§ Mon profil</Link>
                        <Link to="/dashboard/assistance">üìû Assistance</Link>
                    </>
                )}
            </nav>
        </div>
    );
};

export default Sidebar;
