// src/components/Sidebar.js
import React from 'react';
import { Link } from 'react-router-dom';
import { useAuth  } from '../../context/AuthContext';
import './Sidebar.css'; // tu peux styliser plus tard

const Sidebar = () => {
    const { user } = useAuth();

    if (!user) return null;

    const role = user.role;

    return (
        <div className="sidebar">
            <h3>Tableau de bord</h3>
            <ul>
                {role === "admin" && (
                    <>
                        <Link to="/dashboard/utilisateurs">üë• Utilisateurs</Link>
                        <Link to="/dashboard/produits">üì¶ Produits</Link>
                        <Link to="/dashboard/commandes">üìã Commandes</Link>
                        <Link to="/dashboard/devis">üìù Devis</Link>
                        <Link to="/dashboard/factures">üí∞ Factures</Link>
                        <Link to="/dashboard/statistiques">üìä Statistiques</Link>
                    </>
                )}
                {user?.role === 'receptionniste' && (
                    <>
                        <li><Link to="/dashboard/ajouter-commande">Ajouter une commande</Link></li>
                        <li><Link to="/dashboard/produits">G√©rer les produits</Link></li>
                        <li><Link to="/dashboard/clients">Voir les clients</Link></li>
                    </>
                )}
                {user?.role === 'client' && (
                    <>
                        <li><Link to="/dashboard/mes-produits">Mes produits</Link></li>
                        <li><Link to="/dashboard/mes-commandes">Mes commandes</Link></li>
                        <li><Link to="/dashboard/mes-devis">Mes devis</Link></li>
                        <li><Link to="/dashboard/mes-factures">Mes factures</Link></li>
                        <li><Link to="/dashboard/profil">Mon profil</Link></li>
                        <li><Link to="/dashboard/support">Assistance</Link></li>
                    </>
                )}
            </ul>
        </div>
    );
};

export default Sidebar;
