import React, { useEffect, useState } from "react";
import axios from "axios";

const API_URL = "http://127.0.0.1:8000/api/produits";

function App() {
  const [produits, setProduits] = useState([]);
  const [nom, setNom] = useState("");
  const [quantite, setQuantite] = useState("");
  const [prix, setPrix] = useState("");

  const chargerProduits = async () => {
    const res = await axios.get(API_URL);
    setProduits(res.data);
  };

  useEffect(() => {
    chargerProduits();
  }, []);

  const ajouterProduit = async (e) => {
    e.preventDefault();
    if (!nom || !quantite || !prix) return alert("Champs requis");

    await axios.post(API_URL, {
      nom,
      quantite: parseInt(quantite),
      prix: parseFloat(prix),
    });

    setNom("");
    setQuantite("");
    setPrix("");
    chargerProduits();
  };

  const supprimerProduit = async (id) => {
    await axios.delete(`${API_URL}/${id}`);
    chargerProduits();
  };

  return (
    <div style={{ padding: "30px" }}>
      <h1>Gestion de Stock</h1>

      <form onSubmit={ajouterProduit} style={{ marginBottom: "20px" }}>
        <input type="text" placeholder="Nom" value={nom} onChange={(e) => setNom(e.target.value)} />
        <input type="number" placeholder="Quantité" value={quantite} onChange={(e) => setQuantite(e.target.value)} />
        <input type="number" placeholder="Prix" step="0.01" value={prix} onChange={(e) => setPrix(e.target.value)} />
        <button type="submit">Ajouter</button>
      </form>

      <table border="1" cellPadding="10" style={{ width: "100%" }}>
        <thead>
          <tr>
            <th>Nom</th>
            <th>Quantité</th>
            <th>Prix (€)</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {produits.map((p) => (
            <tr key={p.id}>
              <td>{p.nom}</td>
              <td>{p.quantite}</td>
              <td>{p.prix}</td>
              <td>
                <button onClick={() => supprimerProduit(p.id)}>Supprimer</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
