import React, { useEffect, useState } from "react";
import axios from "axios";

const Utilisateurs = () => {
    const [utilisateurs, setUtilisateurs] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchUtilisateurs = async () => {
            try {
                setError(null);
                // 1. CSRF cookie pour Sanctum
                await axios.get("http://127.0.0.1:8000/sanctum/csrf-cookie", {
                    withCredentials: true,
                });

                // 2. Récupère token du localStorage
                const token = localStorage.getItem("token");
                if (!token) {
                    setError("Token non trouvé, veuillez vous connecter.");
                    return;
                }

                // 3. Requête API utilisateurs avec token Bearer
                const response = await axios.get("http://127.0.0.1:8000/api/utilisateurs", {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                    withCredentials: true,
                });

                setUtilisateurs(response.data);
            } catch (err) {
                console.error(err);
                setError("Erreur lors du chargement des utilisateurs");
            }
        };

        fetchUtilisateurs();
    }, []);

    return (
        <div>
            <h2>Gestion des utilisateurs</h2>
            {error && <p style={{ color: "red" }}>{error}</p>}

            <ul>
                {utilisateurs.length === 0 && !error && <li>Aucun utilisateur trouvé.</li>}
                {utilisateurs.map((u) => (
                    <li key={u.id}>{u.nom} {u.prenom} ({u.email})</li>
                ))}
            </ul>
        </div>
    );
};

export default Utilisateurs;
