import React, { useEffect, useState } from 'react';
import api from '../../services/api'; // adapte selon ton chemin
import './Utilisateurs.css'; // si tu veux du style

const Utilisateurs = () => {
    const [utilisateurs, setUtilisateurs] = useState([]);
    const [loading, setLoading] = useState(true);
    const [form, setForm] = useState({ nom: '', prenom: '', email: '', role: 'client' });
    const [editingId, setEditingId] = useState(null);

    useEffect(() => {
        fetchUtilisateurs();
    }, []);

    const fetchUtilisateurs = async () => {
        try {
            const res = await api.get('/utilisateurs');
            setUtilisateurs(res.data);
        } catch (err) {
            console.error('Erreur chargement utilisateurs', err);
        } finally {
            setLoading(false);
        }
    };

    const handleChange = (e) => {
        setForm({ ...form, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            if (editingId) {
                await api.put(`/utilisateurs/${editingId}`, form);
            } else {
                await api.post('/utilisateurs', form);
            }
            setForm({ nom: '', prenom: '', email: '', role: 'client' });
            setEditingId(null);
            fetchUtilisateurs();
        } catch (err) {
            console.error('Erreur lors de la soumission', err);
        }
    };

    const handleEdit = (utilisateur) => {
        setForm({
            nom: utilisateur.nom,
            prenom: utilisateur.prenom,
            email: utilisateur.email,
            role: utilisateur.role,
        });
        setEditingId(utilisateur.id);
    };

    const handleDelete = async (id) => {
        if (window.confirm('Confirmer la suppression ?')) {
            try {
                await api.delete(`/utilisateurs/${id}`);
                fetchUtilisateurs();
            } catch (err) {
                console.error('Erreur suppression utilisateur', err);
            }
        }
    };

    return (
        <div>
            <h2>Gestion des utilisateurs</h2>
            <form onSubmit={handleSubmit} style={{ marginBottom: '20px' }}>
                <input name="nom" placeholder="Nom" value={form.nom} onChange={handleChange} required />
                <input name="prenom" placeholder="Prénom" value={form.prenom} onChange={handleChange} required />
                <input name="email" placeholder="Email" value={form.email} onChange={handleChange} required />
                <select name="role" value={form.role} onChange={handleChange}>
                    <option value="admin">Admin</option>
                    <option value="receptionniste">Réceptionniste</option>
                    <option value="client">Client</option>
                </select>
                <button type="submit">{editingId ? "Modifier" : "Ajouter"}</button>
            </form>

            {loading ? (
                <p>Chargement...</p>
            ) : (
                <table>
                    <thead>
                    <tr>
                        <th>Nom</th>
                        <th>Prénom</th>
                        <th>Email</th>
                        <th>Rôle</th>
                        <th>Actions</th>
                    </tr>
                    </thead>
                    <tbody>
                    {utilisateurs.map((u) => (
                        <tr key={u.id}>
                            <td>{u.nom}</td>
                            <td>{u.prenom}</td>
                            <td>{u.email}</td>
                            <td>{u.role}</td>
                            <td>
                                <button onClick={() => handleEdit(u)}>Modifier</button>
                                <button onClick={() => handleDelete(u.id)}>Supprimer</button>
                            </td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default Utilisateurs;
