import React, { useEffect, useState, useContext } from "react";
import api from "../../services/api";
import { AuthContext } from "../../context/AuthContext";

const Utilisateurs = () => {
    const { user } = useContext(AuthContext);
    const [utilisateurs, setUtilisateurs] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // Formulaire d'ajout
    const [formData, setFormData] = useState({
        nom: "",
        prenom: "",
        email: "",
        password: "",
        role: "client",
    });
    const [formError, setFormError] = useState(null);
    const [formSuccess, setFormSuccess] = useState(null);

    useEffect(() => {
        fetchUtilisateurs();
    }, []);

    const fetchUtilisateurs = async () => {
        setLoading(true);
        setError(null);
        try {
            const response = await api.get("/utilisateurs");
            setUtilisateurs(response.data);
        } catch (err) {
            setError("Erreur lors du chargement des utilisateurs.");
        } finally {
            setLoading(false);
        }
    };

    const handleDelete = async (id) => {
        if (!window.confirm("Voulez-vous vraiment supprimer cet utilisateur ?")) return;

        try {
            await api.delete(`/utilisateurs/${id}`);
            setUtilisateurs(utilisateurs.filter((u) => u.id !== id));
        } catch {
            alert("Erreur lors de la suppression.");
        }
    };

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setFormError(null);
        setFormSuccess(null);

        try {
            await api.post('/utilisateurs', formData);
            fetchUtilisateurs();
            setFormSuccess("Utilisateur ajouté avec succès !");
            setFormData({
                nom: "",
                prenom: "",
                email: "",
                password: "",
                role: "client",
            });
        } catch (err) {
            if (err.response) {
                console.error(err.response.data);
                setFormError("Erreur lors de l'ajout de l'utilisateur : " + (err.response.data.message || "Erreur serveur"));
            } else {
                setFormError("Erreur de connexion au serveur");
            }
        }
    };

    if (!user) {
        return <p>Chargement...</p>;
    }

    return (
        <div>
            <h2>Gestion des utilisateurs</h2>

            {error && <p style={{ color: "red" }}>{error}</p>}
            {loading ? (
                <p>Chargement des utilisateurs...</p>
            ) : (
                <table border="1" cellPadding="10" style={{ width: "100%", marginBottom: "2rem" }}>
                    <thead>
                    <tr>
                        <th>ID</th>
                        <th>Nom</th>
                        <th>Prénom</th>
                        <th>Email</th>
                        <th>Rôle</th>
                        <th>Actions</th>
                    </tr>
                    </thead>
                    <tbody>
                    {utilisateurs.map((u) => (
                        <tr key={u.id}>
                            <td>{u.id}</td>
                            <td>{u.nom}</td>
                            <td>{u.prenom}</td>
                            <td>{u.email}</td>
                            <td>{u.role}</td>
                            <td>
                                <button onClick={() => handleDelete(u.id)}>Supprimer</button>
                            </td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            )}

            <h3>Ajouter un utilisateur</h3>
            {formError && <p style={{ color: "red" }}>{formError}</p>}
            {formSuccess && <p style={{ color: "green" }}>{formSuccess}</p>}

            <form onSubmit={handleSubmit} style={{ maxWidth: 400 }}>
                <div>
                    <input
                        type="text"
                        name="nom"
                        placeholder="Nom"
                        value={formData.nom}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div>
                    <input
                        type="text"
                        name="prenom"
                        placeholder="Prénom"
                        value={formData.prenom}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div>
                    <input
                        type="email"
                        name="email"
                        placeholder="Email"
                        value={formData.email}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div>
                    <input
                        type="password"
                        name="password"
                        placeholder="Mot de passe"
                        value={formData.password}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div>
                    <select name="role" value={formData.role} onChange={handleChange}>
                        <option value="admin">Admin</option>
                        <option value="receptionniste">Réceptionniste</option>
                        <option value="client">Client</option>
                    </select>
                </div>
                <button type="submit" style={{ marginTop: 10 }}>
                    Ajouter
                </button>
            </form>
        </div>
    );
};

export default Utilisateurs;
