import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
// import { FaUserCircle } from 'react-icons/fa';
import './Navbar.css';


function Navbar() {

    const navigate = useNavigate();

    // Simple check connexion
    const isLoggedIn = () => !!localStorage.getItem('user');

    // Handler clic sur liens protégés
    const handleProtectedClick = (e, path) => {
        if (!isLoggedIn()) {
            e.preventDefault(); // bloque la navigation
            alert('Veuillez vous connecter pour accéder à cette page.');
            navigate('/login');
        }
    };
    return (

        <header className="navbar-ogani">
            <div className="container-ogani">
                <div className="logo">
                    <Link to="/">Plutina</Link>
                </div>

                <nav className="menu">
                    <Link to="/">Accueil</Link>
                    <Link to="/produits" onClick={(e) => handleProtectedClick(e, '/produits')}>Produits</Link>
                    <Link to="/commandes" onClick={(e) => handleProtectedClick(e, '/produits')}>Commandes</Link>
                    <Link to="/devis" onClick={(e) => handleProtectedClick(e, '/produits')}>Devis</Link>
                    <Link to="/factures">Factures</Link>
                    <Link to="/admin">Admin</Link>
                </nav>

                <div className="profile">
                    <Link to="/profil">
                        {/*<FaUserCircle size={28} />*/}
                    </Link>
                </div>
            </div>
        </header>

    );
}

export default Navbar;
