import React, { useEffect, useState, useContext } from "react";
import axios from "axios";
import { AuthContext } from "../../context/AuthContext";

const Utilisateurs = () => {
    const { user } = useContext(AuthContext);
    const [utilisateurs, setUtilisateurs] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchUtilisateurs = async () => {
            if (!user) {
                setError("Veuillez vous connecter");
                return;
            }

            try {
                setError(null);
                await axios.get("http://127.0.0.1:8000/sanctum/csrf-cookie", {
                    withCredentials: true,
                });

                const token = user.token; // ou autre selon ta structure user

                const response = await axios.get("http://127.0.0.1:8000/api/utilisateurs", {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                    withCredentials: true,
                });

                setUtilisateurs(response.data);
            } catch (err) {
                console.error(err);
                setError("Erreur lors du chargement des utilisateurs");
            }
        };

        fetchUtilisateurs();
    }, [user]);

    return (
        <div>
            <h2>Gestion des utilisateurs</h2>
            {error && <p style={{ color: "red" }}>{error}</p>}

            <ul>
                {utilisateurs.length === 0 && !error && <li>Aucun utilisateur trouv√©.</li>}
                {utilisateurs.map((u) => (
                    <li key={u.id}>{u.nom} {u.prenom} ({u.email})</li>
                ))}
            </ul>
        </div>
    );
};

export default Utilisateurs;
