import React, { useEffect, useState, useContext } from 'react';
import api from '../../services/api';
import { AuthContext } from '../../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Utilisateurs = () => {
    const { logout } = useContext(AuthContext);
    const navigate = useNavigate();

    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // Fonction pour charger la liste des utilisateurs
    const fetchUsers = async () => {
        setLoading(true);
        setError(null);

        try {
            // Appeler le cookie CSRF (important pour Sanctum)
            await axios.get('http://127.0.0.1:8000/sanctum/csrf-cookie', { withCredentials: true });

            // Requête API authentifiée grâce à interceptor (token dans headers)
            const res = await api.get('/utilisateurs');

            setUsers(res.data);
        } catch (err) {
            if (err.response && err.response.status === 401) {
                setError('Token non trouvé ou expiré, veuillez vous connecter.');
                logout();
                navigate('/login');
            } else {
                setError('Erreur lors du chargement des utilisateurs.');
            }
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchUsers();
    }, []);

    // Supprimer un utilisateur
    const handleDelete = async (id) => {
        if (!window.confirm('Voulez-vous vraiment supprimer cet utilisateur ?')) return;

        try {
            await api.delete(`/utilisateurs/${id}`);
            setUsers(users.filter(user => user.id !== id));
        } catch (err) {
            alert('Erreur lors de la suppression.');
        }
    };

    if (loading) return <p>Chargement...</p>;

    return (
        <div>
            <h2>Gestion des utilisateurs</h2>
            {error && <p style={{color: 'red'}}>{error}</p>}

            <table border="1" cellPadding="8" style={{ width: '100%', borderCollapse: 'collapse' }}>
                <thead>
                <tr>
                    <th>Nom</th>
                    <th>Prénom</th>
                    <th>Email</th>
                    <th>Rôle</th>
                    <th>Actions</th>
                </tr>
                </thead>
                <tbody>
                {users.length === 0 && (
                    <tr><td colSpan="5">Aucun utilisateur trouvé.</td></tr>
                )}
                {users.map(user => (
                    <tr key={user.id}>
                        <td>{user.nom}</td>
                        <td>{user.prenom}</td>
                        <td>{user.email}</td>
                        <td>{user.role}</td>
                        <td>
                            {/* Ici tu peux ajouter modifier, voir etc */}
                            <button onClick={() => handleDelete(user.id)} style={{color: 'red'}}>Supprimer</button>
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );
};

export default Utilisateurs;
