import React, { useEffect, useState } from "react";
import api from "../../services/api";
import { useNavigate } from "react-router-dom";

const AjouterCommande = () => {
    const [produits, setProduits] = useState([]);
    const [clients, setClients] = useState([]);
    const [form, setForm] = useState({
        id_client: "",
        debut_location: "",
        fin_location: "",
        produits: [],
    });
    const [message, setMessage] = useState("");
    const navigate = useNavigate();

    useEffect(() => {
        const fetchData = async () => {
            const produitsRes = await api.get("/produits");
            const clientsRes = await api.get("/clients");
            setProduits(produitsRes.data.data || []);
            setClients(clientsRes.data || []);
        };
        fetchData();
    }, []);

    const handleProduitChange = (id, field, value) => {
        setForm((prevForm) => {
            const updatedProduits = prevForm.produits.map((p) =>
                p.id === id ? { ...p, [field]: value } : p
            );
            return { ...prevForm, produits: updatedProduits };
        });
    };

    const addProduit = (id) => {
        const exist = form.produits.find((p) => p.id === id);
        if (!exist) {
            setForm({
                ...form,
                produits: [...form.produits, { id, quantite: 1, prix_unitaire: 0 }],
            });
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await api.post("/commandes", form);
            setMessage("Commande ajoutée avec succès !");
            navigate("/dashboard/commandes");
        } catch (error) {
            console.error("Erreur création commande :", error);
            setMessage("Erreur lors de la création de la commande.");
        }
    };

    return (
        <div className="container mt-5">
            <h2>Ajouter une commande</h2>
            {message && <div className="alert alert-info">{message}</div>}
            <form onSubmit={handleSubmit}>
                <div className="mb-3">
                    <label>Client</label>
                    <select
                        className="form-control"
                        value={form.id_client}
                        onChange={(e) => setForm({ ...form, id_client: e.target.value })}
                        required
                    >
                        <option value="">-- Choisir un client --</option>
                        {clients.map((client) => (
                            <option key={client.id} value={client.id}>
                                {client.nom} {client.prenom}
                            </option>
                        ))}
                    </select>
                </div>

                <div className="mb-3">
                    <label>Date début</label>
                    <input
                        type="datetime-local"
                        className="form-control"
                        value={form.debut_location}
                        onChange={(e) => setForm({ ...form, debut_location: e.target.value })}
                        required
                    />
                </div>

                <div className="mb-3">
                    <label>Date fin</label>
                    <input
                        type="datetime-local"
                        className="form-control"
                        value={form.fin_location}
                        onChange={(e) => setForm({ ...form, fin_location: e.target.value })}
                        required
                    />
                </div>

                <div className="mb-3">
                    <label>Produits</label>
                    <ul className="list-group">
                        {produits.map((produit) => (
                            <li key={produit.id} className="list-group-item">
                                <span>{produit.designation}</span>
                                <button
                                    type="button"
                                    className="btn btn-sm btn-primary ms-2"
                                    onClick={() => addProduit(produit.id)}
                                >
                                    Ajouter
                                </button>
                            </li>
                        ))}
                    </ul>
                </div>

                {form.produits.length > 0 && (
                    <>
                        <h5 className="mt-4">Produits sélectionnés</h5>
                        {form.produits.map((p, index) => (
                            <div key={index} className="row mb-2">
                                <div className="col-4">
                                    <input
                                        type="number"
                                        placeholder="Quantité"
                                        className="form-control"
                                        value={p.quantite}
                                        onChange={(e) =>
                                            handleProduitChange(p.id, "quantite", e.target.value)
                                        }
                                        required
                                    />
                                </div>
                                <div className="col-4">
                                    <input
                                        type="number"
                                        placeholder="Prix unitaire"
                                        className="form-control"
                                        value={p.prix_unitaire}
                                        onChange={(e) =>
                                            handleProduitChange(p.id, "prix_unitaire", e.target.value)
                                        }
                                        required
                                    />
                                </div>
                            </div>
                        ))}
                    </>
                )}

                <button type="submit" className="btn btn-success mt-3">
                    Enregistrer la commande
                </button>
            </form>
        </div>
    );
};

export default AjouterCommande;
