import React, { useEffect, useState } from 'react';
import api from '../../services/api'; // adapte selon ton chemin
// import './Utilisateurs.css'; // si tu veux du style


const Utilisateurs = () => {
    const [utilisateurs, setUtilisateurs] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // Fonction pour récupérer token depuis localStorage
    const getToken = () => {
        const storedUser = localStorage.getItem('user');
        if (storedUser) {
            const user = JSON.parse(storedUser);
            return user.token || null;
        }
        return null;
    };

    const fetchUtilisateurs = async () => {
        try {
            setLoading(true);
            setError(null);

            // 1. Récupérer le cookie CSRF
            await axios.get('http://127.0.0.1:8000/sanctum/csrf-cookie', {
                withCredentials: true,
            });

            // 2. Appel API utilisateurs avec token Bearer
            const token = getToken();

            const response = await axios.get('http://127.0.0.1:8000/api/utilisateurs', {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
                withCredentials: true,
            });

            setUtilisateurs(response.data);
        }catch (err) {
            console.error('Erreur API utilisateurs:', err);
            if (err.response) {
                setError(`Erreur API: ${err.response.status} - ${err.response.data.message || err.response.statusText}`);
            } else if (err.request) {
                setError('Erreur: pas de réponse du serveur');
            } else {
                setError('Erreur: ' + err.message);
            }
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchUtilisateurs();
    }, []);

    if (loading) return <p>Chargement des utilisateurs...</p>;
    if (error) return <p style={{ color: 'red' }}>{error}</p>;

    return (
        <div>
            <h2>Gestion des utilisateurs</h2>
            <p>Liste, ajout, modification, suppression des utilisateurs.</p>

            <table border="1" cellPadding="5" style={{ width: '100%', borderCollapse: 'collapse' }}>
                <thead>
                <tr>
                    <th>ID</th>
                    <th>Nom</th>
                    <th>Prénom</th>
                    <th>Email</th>
                    <th>Rôle</th>
                </tr>
                </thead>
                <tbody>
                {utilisateurs.length > 0 ? (
                    utilisateurs.map((user) => (
                        <tr key={user.id}>
                            <td>{user.id}</td>
                            <td>{user.nom}</td>
                            <td>{user.prenom}</td>
                            <td>{user.email}</td>
                            <td>{user.role}</td>
                        </tr>
                    ))
                ) : (
                    <tr>
                        <td colSpan="5" style={{ textAlign: 'center' }}>
                            Aucun utilisateur trouvé.
                        </td>
                    </tr>
                )}
                </tbody>
            </table>
        </div>
    );
};

export default Utilisateurs;
