// src/pages/Produits/FicheProduitAdmin.js

import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import api from "../../services/api";

const FicheProduitAdmin = () => {
    const { id } = useParams(); // récupère l’ID du produit depuis l’URL
    const navigate = useNavigate();
    const [produit, setProduit] = useState(null);

    useEffect(() => {
        api.get(`/produits/${id}`)
            .then(res => setProduit(res.data))
            .catch(err => console.error("Erreur de chargement :", err));
    }, [id]);

    const supprimerProduit = () => {
        if (window.confirm("Supprimer ce produit ?")) {
            api.delete(`/produits/${id}`)
                .then(() => {
                    alert("Produit supprimé !");
                    navigate("/produits");
                })
                .catch(err => console.error("Erreur suppression :", err));
        }
    };

    if (!produit) return <p>Chargement...</p>;

    return (
        <div className="p-4 max-w-xl mx-auto bg-white rounded-xl shadow">
            <h2 className="text-2xl font-bold mb-4">Fiche Produit</h2>
            <p><strong>Nom :</strong> {produit.nom}</p>
            <p><strong>Catégorie :</strong> {produit.categorie}</p>
            <p><strong>Prix :</strong> {produit.prix} Ar</p>
            <p><strong>Stock :</strong> {produit.stock}</p>
            <p><strong>Statut :</strong> {produit.actif ? "Actif" : "Inactif"}</p>
            <p><strong>Créé le :</strong> {new Date(produit.created_at).toLocaleString()}</p>

            <div className="mt-6 flex gap-4">
                <button
                    className="bg-yellow-500 text-white px-4 py-2 rounded"
                    onClick={() => navigate(`/produits/${id}/modifier`)}
                >
                    Modifier
                </button>
                <button
                    className="bg-red-500 text-white px-4 py-2 rounded"
                    onClick={supprimerProduit}
                >
                    Supprimer
                </button>
            </div>
        </div>
    );
};

export default FicheProduitAdmin;
