import React, { useEffect, useState } from "react";
import api from "../../services/api";  // axios configuré avec baseURL
import { useNavigate } from "react-router-dom";

function AjouterCommande() {
    const navigate = useNavigate();

    const [clients, setClients] = useState([]);
    const [produits, setProduits] = useState([]);

    const [formData, setFormData] = useState({
        id_client: "",
        produits_selectionnes: [], // tableau des ids produits
        debut_location: "",
        fin_location: "",
    });

    const [errors, setErrors] = useState({});
    const [loading, setLoading] = useState(false);

    // Charger clients et produits pour les selects
    useEffect(() => {
        const fetchData = async () => {
            try {
                const resClients = await api.get("/clients");
                setClients(resClients.data);

                const resProduits = await api.get("/produits");
                setProduits(resProduits.data.data || resProduits.data); // selon API
            } catch (err) {
                console.error("Erreur chargement clients ou produits", err);
            }
        };
        fetchData();
    }, []);

    // Gestion changement champs
    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;

        if (name === "produits_selectionnes") {
            // Ajout/suppression d'un produit sélectionné
            let updatedProduits = [...formData.produits_selectionnes];
            if (checked) {
                updatedProduits.push(value);
            } else {
                updatedProduits = updatedProduits.filter((id) => id !== value);
            }
            setFormData({ ...formData, produits_selectionnes: updatedProduits });
        } else {
            setFormData({ ...formData, [name]: value });
        }
    };

    // Soumettre formulaire
    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setErrors({});

        // Préparer données à envoyer
        const dataToSend = {
            id_client: formData.id_client,
            produits: formData.produits_selectionnes,
            debut_location: formData.debut_location,
            fin_location: formData.fin_location,
        };

        try {
            await api.post("/commandes", dataToSend);
            alert("Commande ajoutée avec succès !");
            navigate("/dashboard/commandes");
        } catch (err) {
            if (err.response && err.response.status === 422) {
                setErrors(err.response.data.errors);
            } else {
                alert("Erreur lors de la création de la commande.");
                console.error(err);
            }
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="container py-5" style={{ maxWidth: 600 }}>
            <h2>Ajouter une nouvelle commande</h2>

            <form onSubmit={handleSubmit}>
                <div className="mb-3">
                    <label>Client</label>
                    <select
                        name="id_client"
                        className={`form-select ${errors.id_client ? "is-invalid" : ""}`}
                        value={formData.id_client}
                        onChange={handleChange}
                        required
                    >
                        <option value="">-- Sélectionner un client --</option>
                        {clients.map((client) => (
                            <option key={client.id} value={client.id}>
                                {client.nom} {client.prenom}
                            </option>
                        ))}
                    </select>
                    {errors.id_client && (
                        <div className="invalid-feedback">{errors.id_client[0]}</div>
                    )}
                </div>

                <div className="mb-3">
                    <label>Produits</label>
                    <div style={{ maxHeight: 200, overflowY: "auto", border: "1px solid #ddd", padding: 10, borderRadius: 5 }}>
                        {produits.map((produit) => (
                            <div className="form-check" key={produit.id}>
                                <input
                                    type="checkbox"
                                    className="form-check-input"
                                    name="produits_selectionnes"
                                    value={produit.id}
                                    id={"produit-" + produit.id}
                                    onChange={handleChange}
                                />
                                <label htmlFor={"produit-" + produit.id} className="form-check-label">
                                    {produit.designation} - {produit.prix} €
                                </label>
                            </div>
                        ))}
                    </div>
                    {errors.produits && (
                        <div className="text-danger">{errors.produits[0]}</div>
                    )}
                </div>

                <div className="mb-3">
                    <label>Date début location</label>
                    <input
                        type="datetime-local"
                        name="debut_location"
                        className={`form-control ${errors.debut_location ? "is-invalid" : ""}`}
                        value={formData.debut_location}
                        onChange={handleChange}
                        required
                    />
                    {errors.debut_location && (
                        <div className="invalid-feedback">{errors.debut_location[0]}</div>
                    )}
                </div>

                <div className="mb-3">
                    <label>Date fin location</label>
                    <input
                        type="datetime-local"
                        name="fin_location"
                        className={`form-control ${errors.fin_location ? "is-invalid" : ""}`}
                        value={formData.fin_location}
                        onChange={handleChange}
                        required
                    />
                    {errors.fin_location && (
                        <div className="invalid-feedback">{errors.fin_location[0]}</div>
                    )}
                </div>

                <button type="submit" className="btn btn-primary" disabled={loading}>
                    {loading ? "Enregistrement..." : "Enregistrer"}
                </button>
            </form>
        </div>
    );
}

export default AjouterCommande;
